/**
 * Returns whether the plugin supports $feature or not.
 *
 * @param string $feature Constant representing the queried feature.
 * @return true | null True if the feature is supported, null otherwise.
 */
function libgeneratortest_supports($feature) {
    switch ($feature) {
        case FEATURE_MOD_INTRO:
            return true;
        case FEATURE_BACKUP_MOODLE2:
            return true;
        default:
            return null;
    }
}
